import React from 'react'
import { useSelector } from 'react-redux'
import { NavLink } from 'react-router-dom';

const Cart = () => {

  const {cart} = useSelector((state) => state);

  return (
    <div>
      {
        cart.length > 0 ?
        (<div>

          <div>
            {
              cart.map((item, index) => (
                <CartItem key={item.id} item={item}></CartItem>
              ))
            }
            </div>
          </div>) :
        (
          <div>
          <h1> Cart Empty </h1>
          <NavLink to="/">
            <button>
              Shop now
            </button>
          </NavLink>

          </div>
        )
      }
    </div>
  )
}

export default Cart
