import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { NavLink } from 'react-router-dom';
import CartItem from '../components/CartItem'

const Cart = () => {

  const {cart} = useSelector((state) => state);
  const [totalSum, SetTotalSum] = useState(0);

  useEffect(() => {
    SetTotalSum(
      cart.reduce((acc, c))
    )
  })

  return (
    <div>
      {
        cart.length > 0 ?
        (<div>

          <div>
            {
              cart.map((item, index) => (
                <CartItem key={item.id} item={item} cartindex={index}></CartItem>
              ))
            }
            </div>
            <div>
              <div>
                  <p>Your Cart</p>
                  <p>Summary</p>
                  <p>
                    <span> Total Items : {cart.length}</span>
                  </p>
                </div>
              </div>
          </div>) :
        (
          <div>
          <h1> Cart Empty </h1>
          <NavLink to="/">
            <button>
              Shop now
            </button>
          </NavLink>

          </div>
        )
      }
    </div>
  )
}

export default Cart
